CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."order"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "customer_id" uuid NOT NULL, "order_status_id" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "shipped_date" date, "store_id" uuid NOT NULL, "staff_id" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("customer_id") REFERENCES "public"."customer"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("order_status_id") REFERENCES "public"."order_status"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("store_id") REFERENCES "public"."store"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("staff_id") REFERENCES "public"."staff"("id") ON UPDATE no action ON DELETE no action);
